using Cysharp.Threading.Tasks;using UnityEngine;namespace SaveLoadSystemNamespace {    public class PlayerPrefsStrategy : ISaveLoadStrategy {        public void Save(ISaveLoadObject saveObject) {            var jsonData = saveObject.GetSaveLoadData();            PlayerPrefs.SetString(saveObject.objectKey, jsonData);        }        public UniTask Load(ISaveLoadObject saveObject) {            var values = PlayerPrefs.GetString(saveObject.objectKey);            saveObject.RestoreValues(values);            return UniTask.CompletedTask;        }    }}