using System;using Cysharp.Threading.Tasks;using UnityEngine;namespace SaveLoadSystemNamespace {    public enum SaveType {        PlayerPrefs = 0,        GamePushCloud = 1    }    public class SaveLoadSystem {        private readonly PlayerPrefsStrategy _playerPrefsStrategy = new();        private readonly GamePushStrategy _gamePushStrategy = new();        public void SaveObject(SaveType saveType, ISaveLoadObject saveObject) {            Debug.Log($"Save requested to {saveType}");            var strategy = GetStrategy(saveType);            strategy.Save(saveObject);        }        public async UniTask LoadObject(SaveType saveType, ISaveLoadObject saveLoadObject) {            var strategy = GetStrategy(saveType);            await strategy.Load(saveLoadObject);        }        private ISaveLoadStrategy GetStrategy(SaveType saveType) {            return saveType switch {                SaveType.PlayerPrefs => _playerPrefsStrategy,                SaveType.GamePushCloud => _gamePushStrategy,                _ => throw new NotImplementedException()            };        }    }}